import { Component } from '@angular/core';

import { products } from '../products';

@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.css'],
})
export class ProductListComponent {
  productos = products;

  compartir() {
    window.alert('El producto ha sido compartido!');
  }

  onNotify() {
    window.alert('Usted será notificado cuando el producto sale a la venta');
  }
}
<h2>Productos</h2>

<div *ngFor="let producto of productos">
  <h3>
    <a
      [title]="'Detalles' + producto.name"
      [routerLink]="['/products', producto.id]">
      {{ producto.name }}
    </a>
  </h3>
  <p *ngIf="producto.description">Description: {{ producto.description }}</p>

  <button type="button" (click)="compartir()">Compartir</button>

  <app-product-alerts [producto]="producto" (notificar)="onNotify()">
  </app-product-alerts>
</div>
